 // Lê todas as linhas do node anterior (Get many rows)
const rows = $input.all().map(i => i.json);

// helpers
const num = v => {
  if (v === null || v === undefined) return 0;
  if (typeof v === 'number') return v;
  if (typeof v === 'string') return Number(v.replace(/\./g,'').replace(',','.')) || 0;
  return 0;
};
const clean = s => (s ?? '').toString().replace(/\s+/g,' ').trim();

const items = rows.map((v, i) => {
  // normaliza categoria
  const cat = String(v.categoria ?? v.tipo ?? 'OUTROS').toUpperCase();

  // preços
  let precoG = num(v.preco_grande ?? v.precoG ?? v.grande ?? v.preco ?? v.valor);
  let precoM = num(v.preco_medio  ?? v.precoM ?? v.medio);

  // se só tiver 1 preço, replica pros dois para não quebrar a UI
  if (!precoG && precoM) precoG = precoM;
  if (!precoM && precoG) precoM = precoG;

  const permiteMeia = (cat === 'PIZZA') && !!precoG;
  const precoMeia   = permiteMeia ? +(precoG / 2).toFixed(2) : null;

  return {
    id:        v.id ?? v.numero ?? v.item_id ?? (i + 1),
    nome:      clean(v.nome ?? v.titulo ?? `Item ${i + 1}`),
    descricao: clean(v.descricao),
    categoria: cat,
    imagem:    v.imagem ?? v.imagem_url ?? '',

    // “principal” (usa grande se existir, senão médio)
    preco: precoG || precoM || 0,

    // tamanhos explícitos
    preco_medio:  precoM || null,
    preco_grande: precoG || null,
    tamanhos: { M: precoM || null, G: precoG || null },

    // meia pizza
    permite_meia: permiteMeia,
    preco_meia:   precoMeia,
  };
});

// IMPORTANTE: o Respond node precisa receber UM OBJETO (não array)
return [{ json: { items, count: items.length, ts: new Date().toISOString() } }];

 // Lê todas as linhas do node anterior
const rows = $input.all().map(i => i.json);

// helpers
const num = v => {
  if (v === null || v === undefined) return 0;
  if (typeof v === 'number') return v;
  if (typeof v === 'string') return Number(v.replace(/\./g,'').replace(',','.')) || 0;
  return 0;
};
const txt = v => (v ?? '').toString().replace(/\s+/g,' ').trim();

const items = rows.map((v, i) => {
  const categoria = String(v.categoria ?? v.tipo ?? 'OUTROS').toUpperCase();
  const isPizza   = categoria === 'PIZZA';

  // Leia todos os campos possíveis vindos do banco
  const rawM = num(v.preco_medio  ?? v.pm ?? v.precoM);
  const rawG = num(v.preco_grande ?? v.pg ?? v.precoG);
  const rawU = num(v.preco ?? v.valor); // preço "único" genérico

  let preco, preco_medio = null, preco_grande = null, tamanhos = [];
  let permite_meia = false, preco_meia = null;

  if (isPizza) {
    // Pizza: usa G se existir, senão M, senão qualquer preço único
    preco_grande = rawG || (rawU && !rawM ? rawU : 0) || null;
    preco_medio  = rawM || null;
    preco        = (preco_grande ?? 0) || (preco_medio ?? 0) || rawU || 0;

    // Só pizza pode meia (metade do G)
    if (preco_grande) {
      permite_meia = true;
      preco_meia   = +(preco_grande / 2).toFixed(2);
    }

    // Botões de tamanhos só para pizza
    tamanhos = [
      ...(preco_medio  ? [{ nome: 'M', preco: preco_medio  }] : []),
      ...(preco_grande ? [{ nome: 'G', preco: preco_grande }] : []),
    ];
  } else {
    // Não-pizza: um único preço (pega o que tiver)
    preco = rawU || rawG || rawM || 0;
    // garante que NÃO saiam campos de tamanho
    preco_medio = null;
    preco_grande = null;
    tamanhos = [];
    permite_meia = false;
    preco_meia = null;
  }

  return {
    id:        v.numero ?? v.id ?? v.item_id ?? (i + 1),
    nome:      txt(v.nome ?? v.titulo ?? `Item ${i + 1}`),
    categoria,
    descricao: txt(v.descricao),
    imagem:    v.imagem ?? v.imagem_url ?? '',
    preco,
    preco_medio,
    preco_grande,
    tamanhos,
    permite_meia,
    preco_meia,
    disponivel: String(v.disponivel ?? '').toLowerCase() !== 'false',
    volume: v.volume ?? null,
  };
});

// **Respond to Webhook** precisa de OBJETO (não array)
return [{ json: { items, count: items.length } }];

 // pega todas as linhas vindas do "Get many rows"
const rows = $input.all().map(i => i.json);

// helper: transforma "64", "53,50", "1.234,56" em número
function num(v){
  if (typeof v === 'number') return v;
  if (typeof v === 'string') return Number(v.replace(/\./g,'').replace(',','.')) || 0;
  return 0;
}

const items = rows.map((v, i) => {
  const categoria = String(v.categoria ?? v.tipo ?? 'OUTROS').toUpperCase();
  const isPizza = categoria === 'PIZZA';

  const precoMedio   = num(v.preco_medio   ?? v.pm ?? v.precoM);
  const precoGrande  = num(v.preco_grande  ?? v.pg ?? v.precoG);
  const precoUnico   = num(v.preco ?? v.valor); // p/ não-pizza

  // “preco” base que a UI pode usar como default:
  const preco = isPizza ? (precoGrande || precoMedio) : (precoUnico || precoMedio || precoGrande);

  // imagem fallback (se não tiver no banco)
  const imagem = v.imagem ?? v.imagem_url ?? '';

  return {
    id: v.numero ?? v.id ?? v.item_id ?? (i + 1),
    nome: v.nome ?? v.titulo ?? `Item ${i + 1}`,
    categoria,
    descricao: v.descricao ?? '',
    imagem,                            // string (ou vazio)
    preco,                             // número (default)
    preco_medio:  isPizza ? precoMedio  : null,
    preco_grande: isPizza ? precoGrande : null,
    // tamanhos opcionais (se quiser usar na UI):
    tamanhos: isPizza
      ? [
          ...(precoMedio  ? [{ nome: 'M', preco: precoMedio  }] : []),
          ...(precoGrande ? [{ nome: 'G', preco: precoGrande }] : []),
        ]
      : [],
    // flags úteis
    disponivel: (String(v.disponivel ?? '').toLowerCase() !== 'false'),
    volume: v.volume ?? null,
  };
});

// IMPORTANTE: Respond to Webhook espera um OBJETO.
// Envelopa o array em { items, count }.
return [{ json: { items, count: items.length } }];
