 // Texto vindo do cliente
const textoOriginal = ( $('MensagemTraduzida').first().json.message || $json.texto || "").toLowerCase();
const texto = textoOriginal
  .normalize("NFD")
  .replace(/[\u0300-\u036f]/g, "") // remove acentos
  .trim();

// Lista de categorias aceitas
const categorias = ["pizza", "pizzas", "esfirra", "borda", "item", "bebida", "refrigerante"];

// Detecta a categoria no texto
let categoriaDetectada = categorias.find(c => texto.includes(c)) || null;

// Se não detectou, assume pizza como padrão
if (!categoriaDetectada) {
  categoriaDetectada = "pizza";
}

// Remove a categoria, plurais e espaços extras
let nome = texto;
if (categoriaDetectada) {
  nome = nome
    .replace(new RegExp("\\b" + categoriaDetectada + "\\b", "gi"), "")
    .replace(/\b(pizzas?|esfirras?)\b/gi, "")
    .replace(/\s{2,}/g, " ")
    .trim();
}

// Retorna JSON estruturado
return [
  {
    json: {
      categoria: categoriaDetectada,
      nome: nome || null
    }
  }
];

 // Pega a mensagem original do item anterior
const msg =  $('When Executed by Another Workflow').first().json.mensagem || "";

// Retorna a resposta formatada
return [
  {
    mensagem: `Foram encontrados mais de 8 itens com a pesquisa ${msg}`
  }
];

