 // Entradas disponíveis no fluxo:
// $input.first().json.categoria
// $input.first().json.nome
// $('MensagemTraduzida').first().json.message

function extractVolume(s) {
  if (!s) return { volume_str: null, volume_ml: null, cleaned: s };

  let str = String(s);

  // 1) Detectar volumes em L (2L, 1,5 L, 1 litro, 2 litros, lt/lts)
  let m = str.match(/\b(\d+(?:[.,]\d+)?)\s*(l|lt|lts|litro?s?)\b/i);
  if (m) {
    const rawNum = m[1].replace(",", "."); // 1,5 => 1.5
    const liters = parseFloat(rawNum);
    if (!isNaN(liters)) {
      const ml = Math.round(liters * 1000);
      // Remover o trecho de volume do nome
      const cleaned = str
        .replace(m[0], "")
        .replace(/\s{2,}/g, " ")
        .trim();
      return { volume_str: `${liters % 1 === 0 ? liters.toFixed(0) : liters}L`, volume_ml: ml, cleaned };
    }
  }

  // 2) Detectar volumes em ML (600ml, 350 ml)
  m = str.match(/\b(\d+)\s*ml\b/i);
  if (m) {
    const ml = parseInt(m[1], 10);
    if (!isNaN(ml)) {
      const cleaned = str
        .replace(m[0], "")
        .replace(/\s{2,}/g, " ")
        .trim();
      return { volume_str: `${ml}ml`, volume_ml: ml, cleaned };
    }
  }

  // Nada encontrado
  return { volume_str: null, volume_ml: null, cleaned: str };
}

function limparNomeBebida(base) {
  if (!base) return base;
  let s = String(base);

  // Remover volumes (vamos extrair com extractVolume; aqui só garantimos limpeza residual)
  s = s
    .replace(/\b\d+[.,]?\d*\s*(l|lt|lts|litro?s?)\b/gi, "")
    .replace(/\b\d+\s*ml\b/gi, "");

  // Remover embalagens/complementos comuns
  s = s.replace(/\b(lata|latao|latão|garrafa|pet|retornavel|retornável|vidro|long\s*neck|pack|fardo|caixa|cx)\b/gi, "");

  // Traços e espaços extras
  s = s.replace(/[-–—•·]+/g, " ").replace(/\s{2,}/g, " ").trim();

  return s;
}

const categoria = String($input.first().json.categoria || "").toLowerCase();
const nomeEntrada = ($input.first().json.nome ?? "").toString();
const mensagem = ($('MensagemTraduzida').first()?.json?.message ?? "").toString();

// Nome base: prioriza o "nome" já extraído; se vazio, tenta a mensagem
const nomeBase = (nomeEntrada || mensagem).trim();

let nome_limpo = nomeBase;
let volume_str = null;
let volume_ml = null;

if (categoria === "bebida" || categoria === "refrigerante") {
  // 1) Extrair volume
  const vol = extractVolume(nomeBase);
  volume_str = vol.volume_str;
  volume_ml = vol.volume_ml;

  // 2) Limpar nome (sem volume/embalagem)
  nome_limpo = limparNomeBebida(vol.cleaned);
}

// Saída
return [
  {
    json: {
      categoria,
      nome_original: nomeBase || null,
      nome_limpo: nome_limpo || null,
      // Volume (se detectado)
      volume: volume_str,     // ex.: "2L" ou "600ml" (string amigável)
      volume_ml: volume_ml,   // ex.: 2000 ou 600 (numérico)
    },
  },
];
