const textoOriginal = (
  $('MensagemTraduzida').first().json.message ||
  $json.texto ||
  ""
).toLowerCase();

let texto = textoOriginal
  .normalize("NFD")
  .replace(/[\u0300-\u036f]/g, "") // remove acentos
  .trim();

// Lista de categorias aceitas
const categorias = ["pizza", "pizzas", "esfirra", "borda", "item", "bebida", "refrigerante"];

// Detecta categoria no texto
let categoriaDetectada = categorias.find(c => texto.includes(c)) || "pizza";

// Remove a categoria e plurais
let nome = texto
  .replace(new RegExp("\\b" + categoriaDetectada + "\\b", "gi"), "")
  .replace(/\b(pizzas?|esfirras?)\b/gi, "")
  .trim();

// 🔹 Remove preposições e conectivos comuns
nome = nome
  .replace(/\b(c\/|com|e|de|da|do|das|dos|na|no|nas|nos|ao|a|o|as|os|à|às)\b/gi, " ")
  .replace(/\s{2,}/g, " ")
  .trim();

// 🔸 Verifica se 'nome' é número inteiro
let numero = 0;
if (/^\d+$/.test(nome)) {
  numero = parseInt(nome, 10);
}

// Retorna JSON limpo
return [
  {
    json: {
      categoria: $('When Executed by Another Workflow').first().json.categoria,
      nome: nome || null,
      numero: numero,
      id_empresa: $('Dados Lead').first().json.id_empresa
    }
  }
];

