// Lista de categorias aceitas
const categorias = ["pizza", "pizzas", "esfirra", "borda", "item", "bebida", "refrigerante"];

// Detecta categoria no texto
let categoriaDetectada = categorias.find(c => texto.includes(c)) || "pizza";

// Remove a categoria e plurais
let nome = texto
  .replace(new RegExp("\\b" + categoriaDetectada + "\\b", "gi"), "")
  .replace(/\b(pizzas?|esfirras?)\b/gi, "")
  .trim();

// üîπ Remove preposi√ß√µes e conectivos comuns
// (com, c/, e, de, da, do, das, dos, na, no, nas, nos, ao, √†, √°s, a, o)
nome = nome
  .replace(/\b(c\/|com|e|de|da|do|das|dos|na|no|nas|nos|ao|a|o|as|os|√†|√†s)\b/gi, " ")
  .replace(/\s{2,}/g, " ")
  .trim();

// Retorna JSON limpo
return [
  {
    json: {
      categoria: $('When Executed by Another Workflow').first().json.categoria ,
      nome: nome || null,
      id_empresa:$('Dados Lead').first().json.id_empresa 

    }
  }
];

 // ‚úÖ Modo: Run Once for All Items
// L√™ todas as linhas que chegaram no node
const rows = $input.all().map(i => i.json);

// Normaliza
const norm = (v) => (v == null ? '' : String(v).trim());
const toNum = (v) => {
  const n = Number(String(v).replace(',', '.'));
  return Number.isFinite(n) ? n : 0;
};

// Filtra s√≥ pizzas v√°lidas (valor > 0 e qtd > 0)
const pizzas = rows
  .map(it => ({
    item_id: norm(it.item_id),
    numero:  norm(it.numero),
    nome:    norm(it.nome),
    tamanho: norm(it.tamanho),
    categoria: norm(it.categoria),
    quantidade: toNum(it.quantidade || 1),
    valor:   toNum(it.valor || 0), // unit√°rio
  }))
  .filter(it =>
    it.categoria === 'pizza' &&
    it.quantidade > 0 &&
    it.valor > 0 &&
    (it.nome || it.numero)
  );

if (pizzas.length === 0) {
  return [{ json: { mensagem: "üß∫ Carrinho vazio de *pizzas* por enquanto. Quer escolher um sabor? üçï" } }];
}

// Mapa posi√ß√£o ‚Üí ids (se quiser usar depois)
const pos2item_id = {};
const pos2numero  = {};

let txt = "üß∫ *Seu carrinho (pizzas):*\n\n";
let total = 0;

pizzas.forEach((it, i) => {
  const pos = i + 1;
  pos2item_id[pos] = it.item_id;
  pos2numero[pos]  = it.numero;

  const nomeOuNumero = it.nome || `(${it.numero})`;
  const subtotal = it.quantidade * it.valor;
  total += subtotal;

  txt += `${pos}Ô∏è‚É£ *${nomeOuNumero}*\n`;
  txt += `üçï ${it.tamanho || '‚Äî'} √ó${it.quantidade}  ¬∑  üí∞ R$ ${it.valor.toFixed(2).replace('.', ',')}\n`;
  txt += `‚Ü≥ Subtotal: *R$ ${subtotal.toFixed(2).replace('.', ',')}*\n\n`;
});

txt += `üßæ *Total:* R$ ${total.toFixed(2).replace('.', ',')}\n`;
txt += `\nüëâ Para adicionar *borda* ou *item*, diga: "adicionar borda cheddar na *posi√ß√£o* 2".`;

// Retorna um √∫nico item com a mensagem montada
return [{
  json: {
    mensagem: txt,
    pos2item_id,
    pos2numero,
    total
  }
}];

 // ‚úÖ Modo: Run Once for All Items
// L√™ array vindo da consulta SQL (resultados)
const rows = $input.first().json.resultados || [];

// Fun√ß√µes auxiliares
const norm = (v) => (v == null ? '' : String(v).trim());
const toNum = (v) => {
  const n = Number(String(v).replace(',', '.'));
  return Number.isFinite(n) ? n : 0;
};

// Verifica se tem algo
if (!Array.isArray(rows) || rows.length === 0) {
