}

// Monta texto formatado (WhatsApp)
let txt = "üçï *Op√ß√µes encontradas:*\n\n";

rows.forEach((it, i) => {
  const pos = i + 1;
  const nome = norm(it.nome) || `(${it.numero})`;
  const desc = norm(it.descricao);
  const precoG = toNum(it.preco_grande);
  const precoM = toNum(it.preco_medio);
  const precoP = toNum(it.preco_pequena);
  const volume = norm(it.volume);

  txt += `${pos}Ô∏è‚É£ *${nome}*\n`;
  if (desc) txt += `üßÄ ${desc}\n`;
  if (volume) txt += `ü•§ ${volume}\n`;

  const precos = [];
  if (precoG > 0) precos.push(`Grande: R$ ${precoG.toFixed(2).replace('.', ',')}`);
  if (precoM > 0) precos.push(`M√©dia: R$ ${precoM.toFixed(2).replace('.', ',')}`);
  if (precoP > 0) precos.push(`Pequena: R$ ${precoP.toFixed(2).replace('.', ',')}`);

  if (precos.length > 0)
    txt += `üí∞ ${precos.join('‚ÄÉ|‚ÄÉ')}\n`;

  txt += '\n';
});

txt += 'üëâ Qual dessas op√ß√µes voc√™ gostaria de pedir?';

// Retorno
return [{ json: { mensagem: txt } }];

 // üîπ Entrada esperada do node anterior
