parameters.query:
 SELECT
  COALESCE(json_agg(row_to_json(t)), '[]'::json) AS resultados
FROM (
  SELECT numero, nome, categoria, tipo, preco_grande, preco_medio, preco_pequena, descricao
  FROM cardapio
  WHERE id_empresa = {{ $json.id_empresa }}
    AND unaccent(lower(categoria)) = unaccent(lower('{{ $json.categoria }}'))
    AND unaccent(lower(nome)) ILIKE '%' || unaccent(lower(TRIM('{{ $json.nome }}'))) || '%'
  ORDER BY numero
) AS t;

