 // Entrada: items -> [{ ordem, descricao, saldo_inicial, saldo_final, diferenca }, ...]
// Saída: { html }

const rows = items.map(i => i.json);
rows.sort((a, b) => Number(a.ordem) - Number(b.ordem));

const fmt = (n) => Number(Math.abs(n || 0)).toLocaleString('pt-BR', {
  minimumFractionDigits: 2,
  maximumFractionDigits: 2
});

// Define natureza (D ou C)
function natureza(desc, valor) {
  const d = String(desc || '').toUpperCase();
  const v = Number(valor) || 0;
  if (d.startsWith('ATIVO')) return v >= 0 ? 'D' : 'C';
  if (d.startsWith('PASSIVO') || d.includes('PATRIMÔNIO')) return v >= 0 ? 'C' : 'D';
  return v >= 0 ? 'D' : 'C';
}

const isSection = (desc) => {
  const d = String(desc || '').toUpperCase();
  return d === 'ATIVO' || d === 'PASSIVO E PL' || d.includes('PATRIMÔNIO');
};

// Totais (seções)
let ativoIni=0, ativoFim=0, ativoDif=0, passIni=0, passFim=0, passDif=0;
for (const r of rows) {
  const d = String(r.descricao || '').toUpperCase();
  if (d === 'ATIVO') {
    ativoIni += Number(r.saldo_inicial) || 0;
    ativoFim += Number(r.saldo_final)   || 0;
    ativoDif += Number(r.diferenca)     || 0;
  } else if (d === 'PASSIVO E PL' || d.includes('PATRIMÔNIO')) {
    passIni += Number(r.saldo_inicial) || 0;
    passFim += Number(r.saldo_final)   || 0;
    passDif += Number(r.diferenca)     || 0;
  }
}
const chkIni = ativoIni - passIni;
const chkFim = ativoFim - passFim;
const chkDif = ativoDif - passDif;

const geradoEm = new Date().toLocaleString('pt-BR');

let html = `<!DOCTYPE html>
<html lang="pt-BR">
<head>
<meta charset="UTF-8">
<title>Balanço Patrimonial</title>
<style>
  :root{ --primary:#1e88e5; --zebra:#f5f9ff; --border:#e5e9f2; --section:#f0f5ff; --total:#eef3fb; }
  body{ font-family:"Segoe UI", Arial, sans-serif; padding:24px; }
  header{ text-align:center; margin-bottom:14px; }
  h1{ margin:0; font-size:20px; }
  .sub{ color:#6b7a90; font-size:12px; }
  table{ width:100%; border-collapse:collapse; font-size:12px; }
  thead th{ background:var(--primary); color:#fff; padding:8px; }
  td, th{ border:1px solid var(--border); padding:6px 10px; }
  tbody tr:nth-child(even){ background:var(--zebra); }
  .sec{ background:var(--section); font-weight:700; }
  .num{ text-align:right; }
  .tot{ background:var(--total); font-weight:700; }
  .center{ text-align:center; }
</style>
</head>
<body>
<header>
  <h1>Balanço Patrimonial</h1>
  <div class="sub">Gerado em ${geradoEm}</div>
</header>
<table>
  <thead>
    <tr>
      <th>Descrição</th>
      <th>Saldo inicial</th><th></th>
      <th>Saldo final</th><th></th>
      <th>Diferença</th><th></th>
    </tr>
  </thead>
  <tbody>`;

for (const r of rows) {
  const ini = Number(r.saldo_inicial) || 0;
  const fim = Number(r.saldo_final)   || 0;
  const dif = Number(r.diferenca)     || 0;
  html += `
    <tr class="${isSection(r.descricao)?'sec':''}">
      <td>${r.descricao}</td>
      <td class="num">${fmt(ini)}</td><td class="center">${natureza(r.descricao, ini)}</td>
      <td class="num">${fmt(fim)}</td><td class="center">${natureza(r.descricao, fim)}</td>
      <td class="num">${fmt(dif)}</td><td class="center">${natureza(r.descricao, dif)}</td>
    </tr>`;
}

html += `
  </tbody>
  <tfoot>
    <tr class="tot">
      <td>Ativo (totais)</td>
      <td class="num">${fmt(ativoIni)}</td><td class="center">${natureza('ATIVO', ativoIni)}</td>
      <td class="num">${fmt(ativoFim)}</td><td class="center">${natureza('ATIVO', ativoFim)}</td>
      <td class="num">${fmt(ativoDif)}</td><td class="center">${natureza('ATIVO', ativoDif)}</td>
    </tr>
    <tr class="tot">
      <td>Passivo + PL (totais)</td>
      <td class="num">${fmt(passIni)}</td><td class="center">${natureza('PASSIVO E PL', passIni)}</td>
      <td class="num">${fmt(passFim)}</td><td class="center">${natureza('PASSIVO E PL', passFim)}</td>
      <td class="num">${fmt(passDif)}</td><td class="center">${natureza('PASSIVO E PL', passDif)}</td>
    </tr>
    <tr class="tot">
      <td>Checagem (Ativo − Passivo/PL)</td>
      <td class="num">${fmt(chkIni)}</td><td class="center">${natureza('ATIVO', chkIni)}</td>
      <td class="num">${fmt(chkFim)}</td><td class="center">${natureza('ATIVO', chkFim)}</td>
      <td class="num">${fmt(chkDif)}</td><td class="center">${natureza('ATIVO', chkDif)}</td>
    </tr>
  </tfoot>
</table>
</body>
</html>`;

return [{ json: { html } }];

