 // === Code node (Run Once for All Items) ===
// Entrada: items -> [{ ordem, grupo, valor_mes, valor_acumulado }, ...]
// Saída: { html } -> para Convert to File (ou Gotenberg)

const rows = items.map(i => i.json);
rows.sort((a, b) => Number(a.ordem) - Number(b.ordem));

const fmt = (n) => Number(n || 0).toLocaleString('pt-BR', {
