});

// Identifica linhas de "resultado" para destacar
const isResultado = (grupo) => {
  const g = String(grupo || '').toUpperCase();
  return g.includes('RESULTADO') || g.includes('LUCRO') || g.includes('PREJU');
};

const geradoEm = new Date().toLocaleString('pt-BR');
const titulo = 'Demonstração do Resultado do Exercício (DRE)';
const subtitulo = `Gerado em ${geradoEm}`;

let html = `<!DOCTYPE html>
<html lang="pt-BR">
<head>
<meta charset="UTF-8">
<title>${titulo}</title>
<style>
  :root{
    --primary:#1e88e5; --zebra:#f5f9ff; --border:#e5e9f2;
    --text:#2c3e50; --muted:#6b7a90; --total:#eef3fb;
  }
  body{ font-family:"Segoe UI", Arial, sans-serif; margin:0; padding:24px; color:var(--text);}
