body{font-family:system-ui,-apple-system,Segoe UI,Roboto,Arial;margin:0;background:#0f172a;color:#e5e7eb}
.wrap{padding:24px;max-width:960px;margin:0 auto}
header{display:flex;justify-content:space-between;align-items:center;margin-bottom:16px;border-bottom:1px solid #1f2937;padding-bottom:10px}
.brand{font-weight:700;color:#f9fafb}.muted{color:#9ca3af}
.badge{background:#1f2937;border:1px solid #1f2937;border-radius:999px;padding:4px 8px;font-size:12px;color:#9ca3af}
.kpis{display:grid;grid-template-columns:repeat(5,1fr);gap:10px}
.card{background:#111827;border:1px solid #1f2937;border-radius:12px;padding:12px}
.t{margin:0 0 6px 0;font-size:11px;color:#9ca3af;text-transform:uppercase;letter-spacing:.06em}
.v{font-size:18px;font-weight:800;color:#f9fafb}
.pos{color:#16a34a}.neg{color:#dc2626}.warn{color:#f59e0b}
.sub{font-size:12px;color:#9ca3af}
.table{width:100%;border-collapse:collapse;font-size:12px;margin-top:12px}
th,td{border-bottom:1px solid #1f2937;padding:6px;text-align:left}
th{color:#9ca3af;text-transform:uppercase;font-size:11px}
footer{margin-top:12px;color:#6b7280;font-size:12px}
`;

const html = `<!doctype html><html lang="pt-BR"><head><meta charset="utf-8">
<meta name="viewport" content="width=device-width,initial-scale=1"><title>KPIs de Balanço (Nível) – ${empresa}</title>
<style>${css}</style></head><body><div class="wrap">
<header><div><div class="brand">KPIs de Balanço – ${empresa}</div><div class="muted">${periodo}</div></div><div class="badge">Gerado em ${nowBR}</div></header>

<section class="kpis">
  <div class="card"><div class="t">Liquidez Corrente (AC/PC)</div><div class="v ${liqClass(cur.liquidez_corrente)}">${fmt2(cur.liquidez_corrente)}</div><div class="sub">vs mês ant.: ${dLiq||'-'}</div></div>
  <div class="card"><div class="t">Endividamento (Passivo/Ativo)</div><div class="v ${endClass(cur.endividamento)}">${fmt2(cur.endividamento)}</div><div class="sub">vs mês ant.: ${dEnd||'-'}</div></div>
  <div class="card"><div class="t">Ativo Total</div><div class="v">${fmtBR(cur.ativo_total)}</div></div>
  <div class="card"><div class="t">Passivo Total</div><div class="v">${fmtBR(cur.passivo_total)}</div></div>
  <div class="card"><div class="t">Patrimônio Líquido</div><div class="v">${fmtBR(cur.pl)}</div></div>
</section>

<section class="card">
  <div class="t">Série (até ${data.length} meses)</div>
  <table class="table">
    <thead><tr>
      <th>Mês</th><th>AC</th><th>PC</th><th>Ativo</th><th>Passivo</th>
      <th>Liquidez</th><th>Endivid.</th><th>ΔLiquidez</th><th>ΔEndivid.</th>
    </tr></thead>
    <tbody>${body}</tbody>
  </table>
</section>

<footer>Fonte: <b>contab.vw_balanco_niveis</b>. Faixas: Liquidez &ge; 2 (ok), 1–2 (atenção), &lt;1 (crítico). Endividamento &le; 0,5 (ok), 0,5–1 (atenção), &gt;1 (alerta).</footer>
</div></body></html>`;

return [{ json: { html } }];
