 // INPUT: items[i].json.body.message.conversation (string livre do usu√°rio)
// OUTPUT (por item):
// {
//   input,                         // texto original
//   ok,                            // true/false
//   forma_pagamento,               // 'pix' | 'cartao' | 'dinheiro' | null
//   subtipo,                       // 'credito' | 'debito' | null (quando cartao)
//   variante_detectada,            // termo que levou √† decis√£o
//   confidence,                    // 0..1
//   needs_confirmation,            // true se amb√≠guo (ex: mencionou 2 formas)
//   troco_valor,                   // n√∫mero (centavos) ou null
//   troco_texto,                   // texto do troco ("troco pra 100")
//   observacao                     // string curta (ex: "sem troco", "nao tenho cartao")
// }

function unaccent(s) {
  return (s || '').normalize('NFD').replace(/[\u0300-\u036f]/g, '');
}
function toCents(numStr) {
  if (!numStr) return null;
  // aceita "100", "100,00", "R$ 50", "50.50"
  let s = String(numStr).replace(/[^\d,.\s]/g, '').trim();
  // se tem v√≠rgula e ponto, prioriza v√≠rgula como decimal (pt-BR)
  if (/,/.test(s) && /\./.test(s)) s = s.replace(/\./g, '').replace(',', '.');
  else if (/,/.test(s) && !/\./.test(s)) s = s.replace(',', '.');
  const v = parseFloat(s);
  return Number.isFinite(v) ? Math.round(v * 100) : null;
}

function detectPagamento(textRaw) {
  const input = String(textRaw || '');
  const txt = unaccent(input).toLowerCase();

  // Emojis e palavras-chave
  const PIX = [
    'pix', 'chave pix', 'qr code', 'qrcode', 'qr-code', 'transferencia', 'transf', 'pix copia e cola', 'copia e cola'
  ];
  const CARTAO = [
    'cartao', 'cartao de credito', 'cartao de debito', 'credito', 'debito',
    'maquina', 'maquininha', 'pos', 'p-o-s', 'visa', 'mastercard', 'elo', 'amex', 'contactless', 'aproximacao'
  ];
  const DINHEIRO = [
    'dinheiro', 'especie', 'em especie', 'cash', 'nota', 'moeda', 'em dinheiro'
  ];

  // Nega√ß√£o pr√≥xima ao termo
  const NEG_PAT = /\b(nao|n√£o|sem)\b/;

  // Helpers de match ‚Äúforte‚Äù (palavra ou emoji)
  const hasWord = (arr) => arr.some(w => new RegExp(`\\b${w.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')}\\b`).test(txt));
  const hasFrag  = (arr) => arr.some(w => txt.includes(w));

  // Captura de troco
  // Exemplos: "troco pra 100", "troco para R$ 50,00", "sem troco"
  let troco_valor = null, troco_texto = null, observacao = null;
  const mSemTroco = txt.match(/\bsem troco\b/);
  if (mSemTroco) {
    observacao = 'sem troco';
  } else {
    const mTroco = txt.match(/\btroco\s*(p(?:ra|ara)?)?\s*(de|para|pra)?\s*([r$]\s*\$?\s*[\d\.,]+|\d+[.,]?\d*)/);
    if (mTroco) {
      troco_texto = mTroco[0];
      troco_valor = toCents(mTroco[3]);
    }
  }

  // Flags por grupo
  const pixStrong = hasWord(['pix']) || hasFrag(['qr code','qrcode','qr-code','chave pix','pix copia e cola','copia e cola']);
  const cartaoCredito = hasWord(['credito']) || hasFrag(['cartao de credito']);
  const cartaoDebito  = hasWord(['debito'])  || hasFrag(['cartao de debito']);
  const cartaoGeneric = hasWord(['cartao']) || hasFrag(['maquina','maquininha','pos','p-o-s']);
  const dinheiroHit   = hasWord(['dinheiro','especie']) || hasFrag(['em especie','em dinheiro','cash','nota','moeda']);

  // Nega√ß√µes espec√≠ficas (ex: "nao tenho cartao", "sem pix", "nao levo dinheiro")
  const negPix      = /\b(nao|n√£o|sem)\b.*\b(pix)\b|\b(pix)\b.*\b(nao|n√£o|sem)\b/.test(txt);
  const negCartao   = /\b(nao|n√£o|sem)\b.*\b(cartao|credito|debito|maquina|maquininha|pos)\b/.test(txt);
  const negDinheiro = /\b(nao|n√£o|sem)\b.*\b(dinheiro|especie|cash|moeda|nota)\b/.test(txt);

  // Votos
  const votes = [];
  if (pixStrong && !negPix) votes.push({tipo:'pix', subtipo:null, conf:0.9, variante:'pix'});
  if ((cartaoCredito || cartaoDebito || cartaoGeneric) && !negCartao) {
    let subtipo = null, conf = 0.8, variante = 'cartao';
    if (cartaoCredito) { subtipo = 'credito'; conf = 0.9; variante = 'cartao_credito'; }
    if (cartaoDebito)  { subtipo = 'debito';  conf = 0.9; variante = 'cartao_debito'; }
    votes.push({tipo:'cartao', subtipo, conf, variante});
  }
  if (dinheiroHit && !negDinheiro) votes.push({tipo:'dinheiro', subtipo:null, conf:0.9, variante:'dinheiro'});

  // Caso nenhum voto positivo, tenta infer√™ncia leve por emojis
  // üí≥ => cartao, üíµ/ü™ô => dinheiro
  if (!votes.length) {
    if (/üí≥/.test(input)) votes.push({tipo:'cartao', subtipo:null, conf:0.6, variante:'emoji_cartao'});
    if (/üíµ|ü™ô/.test(input)) votes.push({tipo:'dinheiro', subtipo:null, conf:0.6, variante:'emoji_dinheiro'});
  }

  // Se h√° mais de um voto, fica amb√≠guo e pede confirma√ß√£o
  let needs_confirmation = false;
  let picked = null;

  if (votes.length === 1) {
    picked = votes[0];
  } else if (votes.length > 1) {
    // Heur√≠stica: preferir "pix" se expl√≠cito; sen√£o cart√£o (se cr√©dito/d√©bito expl√≠cito); sen√£o dinheiro
    const byType = (t)=>votes.find(v=>v.tipo===t && v.conf>=0.85);
    picked = byType('pix') || byType('cartao') || byType('dinheiro') || votes.sort((a,b)=>b.conf-a.conf)[0];
    needs_confirmation = true;
  }

  const out = {
    input: input,
    ok: !!picked,
    forma_pagamento: picked ? picked.tipo : null,     // 'pix' | 'cartao' | 'dinheiro'
    subtipo: picked ? picked.subtipo : null,          // 'credito' | 'debito' | null
    variante_detectada: picked ? picked.variante : null,
    confidence: picked ? picked.conf : 0,
    needs_confirmation,
    troco_valor,
    troco_texto,
    observacao
  };

  // Regras extras:
  // - Se usu√°rio disse "sem troco" e forma = dinheiro -> aumenta confian√ßa
  if (out.forma_pagamento === 'dinheiro' && observacao === 'sem troco' && out.confidence < 0.9) {
    out.confidence = 0.9;
  }
  // - Se mencionou "nao tenho cartao" e nenhum outro m√©todo, marca ok=false
  if (/nao tenho cartao|n√£o tenho cartao/.test(txt) && !pixStrong && !dinheiroHit) {
    out.ok = false;
    out.observacao = 'nao tem cartao';
  }

  return out;
}

return items.map(i => {
 const e = i.json?.tipo_cobranca ?? '';
  const r = detectPagamento(e);
  return { json: r };
});

