 // n8n Code node (Run Once for All Items)

// Normaliza volume (2L etc.)
function normVol(v) {
  if (!v) return '';
  v = String(v).trim().toLowerCase();
  if (['un','uni','unid','unidade'].includes(v)) return '';
  v = v
    .replace(' litros','l').replace(' litro','l')
    .replace('litros','l').replace('litro','l')
    .replace(' lt','l').replace(' l','l')
    .trim();
  return v ? ' ' + v.toUpperCase() : '';
}

// Pega a lista de rejeitados de onde estiver (itens_rejeitados, coalesce, etc.)
function getSrc(items) {
  const j = (items[0]?.json) ?? {};
  if (Array.isArray(j.itens_rejeitados)) return j.itens_rejeitados;
  if (Array.isArray(j.coalesce)) return j.coalesce;
  if (Array.isArray(j.rejeitados)) return j.rejeitados;
  // tenta achar qualquer propriedade que seja array
  const arrKey = Object.keys(j).find(k => Array.isArray(j[k]));
  if (arrKey) return j[arrKey];
  // fallback: cada item de entrada √© um rejeitado
  return items.map(i => i.json);
}

const raw = getSrc(items) || [];

// **Fix principal**: n√£o use truthiness; use checagem por "definido"
const src = raw
  .filter(x => x != null)
  .map(x => (typeof x === 'object' && x !== null ? x : { numero: x }))
  // defaults seguros
  .map(o => ({
    numero: (o.numero === undefined || o.numero === null || o.numero === '')
              ? 0
              : Number.isFinite(Number(o.numero)) ? Number(o.numero) : 0,
    nome: (typeof o.nome === 'string' && o.nome.trim() !== '')
              ? o.nome
              : 'n√£o encontrado',
    volume: (o.volume ?? '').toString().trim(),
  }))
  // mant√©m itens que tenham pelo menos um campo "presente" (mesmo que 0/'n√£o encontrado')
  .filter(i => i.nome != null || i.numero != null);

const tem_rejeitados = src.length > 0;

let linhas = '';
let mensagem = '';

if (tem_rejeitados) {
  linhas = src
    .map(i => `- ${i.nome && i.nome.trim() !== '' ? i.nome : (i.numero != null ? `Pizza ${i.numero}` : 'n√£o encontrado')}${normVol(i.volume)}`)
    .join('\n');

  mensagem = `üìü Seu(s) item(s) foi(ram) processado(s) com sucesso ‚úÖ
‚ùå O seguinte item n√£o foi reconhecido e ser√°(√£o) exclu√≠do(s):
${linhas}

üö´ Esse item foi ignorado e exclu√≠do do pedido.
üîÅ Deseja reenviar apenas esse item agora?
üìÅ Para consultar o pedido atual, digite "consultar".
üëâ Duvida? Digite -> 2 para instru√ß√µes`;
} else {
  mensagem = `üìü Seu(s) item(s) foi(ram) processado(s) com sucesso ‚úÖ.`;

}

return [{ json: { mensagem, linhas, qtd: src.length, tem_rejeitados } }];

