const raw =
  $json.message ??
  ($items('MensagemTraduzida', 0, 0)[0]?.json?.message ?? '');

// Normaliza texto (sem acento/ru√≠do)
const clean = String(raw)
  .normalize('NFD').replace(/[\u0300-\u036f]/g, '')
  .toLowerCase();

// Levenshtein simples para ‚Äúacertar‚Äù erros de fala/ASR
function lev(a, b) {
  const m = [];
  const al = a.length, bl = b.length;
  for (let i = 0; i <= al; i++) { m[i] = [i]; }
  for (let j = 1; j <= bl; j++) { m[0][j] = j; }
  for (let i = 1; i <= al; i++) {
    for (let j = 1; j <= bl; j++) {
      m[i][j] = (a[i - 1] === b[j - 1])
        ? m[i - 1][j - 1]
        : 1 + Math.min(m[i - 1][j], m[i][j - 1], m[i - 1][j - 1]);
    }
  }
  return m[al][bl];
}

// Tokens para checagens
const tokens = clean.replace(/[^a-z0-9 ]+/g, ' ').split(/\s+/).filter(Boolean);
const has = (re) => re.test(' ' + clean + ' ');

// --- Regras / sin√¥nimos / erros comuns ---
// PIX (inclui erros t√≠picos de ASR: peace/peeks/pics/picks etc.)
const rePix = /\b(pix|chave pix|qr|qrcode|transfer(?:encia)?|peace|peeks?|peaks?|pics?|picks?)\b/;
// Cart√£o
const reCard = /\b(cartao|cartao de credito|credito|debito|card|visa|master|elo|maquininha|pos|no cartao)\b/;
// Dinheiro
const reCash = /\b(dinheiro|cash|especie|troco|cedula|nota)\b/;

// Fuzzy para PIX (palavra curta => tolerar at√© dist√¢ncia 2)
const fuzzyPix = tokens.some(t => lev(t, 'pix') <= 2);

// Sinais
const isPix = has(rePix) || fuzzyPix;
const isCard = has(reCard);
const isCash = has(reCash);

// Decis√£o (ajuste a prioridade se quiser)
let pagamento = 'Cart√£o';
if (isPix) pagamento = 'Pix';
else if (isCard) pagamento = 'Cart√£o';
else if (isCash) pagamento = 'Dinheiro';

return {
  ...$json,
  pagamento,           // <- valor normalizado p/ sua constraint
  message_raw: raw,    // (opcional) debug
  message_clean: clean // (opcional) debug
};

