 // Code node (JavaScript) — Run Once for All Items
// Captura fracionadas e remove do texto para a LLM

const inputItems = $input.all();   // <- aqui está a correção
const out = [];

for (const { json } of inputItems) {
  const raw = String(json.chatInput ?? '');

  // normalização leve
  let norm = raw
    .toLowerCase()
    .replace(/[–—−]/g, '-')               // hífens
    .replace(/\b(meia|metade)\b/g, '1/2') // "meia/metade" -> 1/2
    .replace(/\s+/g, ' ')
    .trim();

  const fracionadas = [];

  // [qtd?][x?] 1/2 A (e|,|+|/) 1/2 B  (evita confundir 2L, etc.)
  const re = /\b(?:(\d+)\s*x?\s*)?1\/2\s*(\d{1,3})(?!\s*[a-z])\s*(?:e|,|\+|\/)\s*1\/2\s*(\d{1,3})(?!\s*[a-z])\b/gi;

  // uma passada: coleta e remove
  let rest = norm.replace(re, (_full, q, a, b) => {
    fracionadas.push({
      quantidade: q ? parseInt(q, 10) : 1,
      metade1: parseInt(a, 10),
      metade2: parseInt(b, 10),
    });
    return ' ';
  });

  rest = rest.replace(/\s+/g, ' ').trim();

  out.push({
    json: {
      ...json,                 // preserva campos existentes (user_id, pedido_id, etc.)
      texto_original: raw,
      texto_limpo: rest,       // passe ESTE texto para a LLM
      fracionadas, 
    telefone:  $input.first().json.telefone
      //depois some com pizzastelefone_fracionadas da LLM
    },
  });
}

return out;

