const mapaQtd = { "uma":"1", "um":"1", "duas":"2", "dois":"2", "três":"3", "tres":"3", "quatro":"4", "cinco":"5" };
if (mapaQtd[tokens[0]]) {
  tokens[0] = mapaQtd[tokens[0]];
  clean = tokens.join(" ");
}

// Normaliza plural básico (caso o pré-LLM não tenha feito)
if (tokens[1] === "pizzas") {
  tokens[1] = "pizza";
  clean = tokens.join(" ");
}

// Resposta padrão (com sugestão educada)
let resultado = {
  original: input,
  comando: clean,
  valido: false,
  tipo: null,
  erro: "não entendi seu comando",
  sugestao: `O seu pedido ou comando não foi identificado. Você quis dizer: ${clean} ?`
};

// --- Caso 1: Pizza inteira ---
// "1 pizza 14", "1 pizza mussarela grande"
if (
  tokens.length >= 3 &&
  /^\d+$/.test(tokens[0]) &&
  tokens[1] === "pizza" &&
  !tokens.includes("1/2")
) {
  resultado = {
    original: input,
    comando: clean,
    valido: true,
    tipo: "pizza_inteira",
    acao: "adicionar_item"
  };
}

// --- Caso 2: Pizza fracionada ---
// "1 pizza 1/2 14 e 1/2 19"  (aceita nomes no lugar de números)
else if (
  /^\d+$/.test(tokens[0]) &&
  tokens[1] === "pizza" &&
  tokens.filter(t => t === "1/2").length === 2
) {
  resultado = {
    original: input,
    comando: clean,
    valido: true,
    tipo: "pizza_fracionada",
    acao: "adicionar_item"
  };
}

// --- Caso 3: Esfirra ---
// "1 esfirra carne"
else if (
  tokens.length >= 3 &&
  /^\d+$/.test(tokens[0]) &&
  tokens[1] === "esfirra"
) {
  resultado = {
    original: input,
    comando: clean,
    valido: true,
    tipo: "esfirra",
    acao: "adicionar_item"
  };
}

// --- Caso 4: Bebida ---
// "1 coca cola 2l", "2 guarana 350ml", "1 água 500ml", "1 heineken long neck"
else if (
  /^\d+$/.test(tokens[0]) &&
  /\b(coca(?:\s+cola)?|cola|guaran[áa]|heineken|bud(?:weiser)?|cerveja|agua|água|fanta|sprite)\b/.test(clean)
) {
  resultado = {
    original: input,
    comando: clean,
    valido: true,
    tipo: "bebida",
    acao: "adicionar_item"
  };
}

// --- Caso 5: Adicionar ingrediente ---
// "adicionar mussarela item 1", "adicionar ingrediente cheddar ao item 3"
else if (
  tokens[0] === "adicionar" &&
  tokens.includes("item")
) {
  resultado = {
    original: input,
    comando: clean,
    valido: true,
    tipo: "adicionar_item",
    acao: "adicionar_item"
  };
}

// --- Caso 6: Excluir item ---
// "excluir item 1", "excluir 2"
else if (
  tokens[0] === "excluir" &&
  (
    (tokens[1] === "item" && /^\d+$/.test(tokens[2])) ||
    (/^\d+$/.test(tokens[1]))
  )
) {
  resultado = {
    original: input,
    comando: clean,
    valido: true,
    tipo: "excluir_item",
    acao: "excluir_item"
  };
}

return resultado;

