const txt = raw
  .normalize("NFD").replace(/[\u0300-\u036f]/g, "")   // remove acentos
  .replace(/[“”"']/g, "")                            // aspas
  .replace(/\s+/g, " ")                              // espaços extras
  .toLowerCase();

// ===== 1) AÇÃO =====
const acaoMatch = txt.match(/\b(adicionar|acrescentar|incluir|colocar)\b/);
const acao = acaoMatch ? acaoMatch[1] : null;

// ===== 2) ITEM DO CARRINHO =====
// padrões aceitos: "ao item 3", "no item 3", "item 3", "#3", "nº 3", ou número no final
let itemNumero = null;
const itemPatterns = [
  /\b(?:ao|no|na)\s+item\s+(\d+)\b/,
  /\bitem\s*(\d+)\b/,
  /\b(?:#|n[ºo.]?)\s*(\d+)\b/,
];
for (const re of itemPatterns) {
  const m = txt.match(re);
  if (m) { itemNumero = parseInt(m[1], 10); break; }
}
// Se ainda não achou: tenta número no final (evita frações tipo 1/2)
if (itemNumero === null && !/\b\d+\/\d+\b/.test(txt)) {
  const endNum = txt.match(/(\d+)\s*$/);
  if (endNum) itemNumero = parseInt(endNum[1], 10);
}

// ===== 3) CATEGORIA =====
let categoria = null;
if (/\bborda\b/.test(txt)) categoria = "borda";
else if (/\bingrediente\b|\bextra\b/.test(txt)) categoria = "ingrediente";
else if (/\bitem\b/.test(txt)) categoria = "item";

// ===== 4) NOME =====
// Remove ação, conectivos e marcador de item; o restante vira nome
let nome = txt;

// remove ação
if (acao) nome = nome.replace(new RegExp("\\b" + acao + "\\b"), "");

// remove conectivos comuns
nome = nome.replace(/\b(de|da|do|das|dos|com|para|pra|ao|no|na)\b/g, " ");

// remove categoria explícita (se houver)
if (categoria) nome = nome.replace(new RegExp("\\b" + categoria + "\\b"), " ");

// remove padrões de "item 3", "ao item 3", "#3", "nº 3" e número final isolado
nome = nome
  .replace(/\b(?:ao|no|na)\s+item\s+\d+\b/g, " ")
  .replace(/\bitem\s*\d+\b/g, " ")
  .replace(/\b(?:#|n[ºo.]?)\s*\d+\b/g, " ")
  .replace(/(\d+)\s*$/g, " ");

// normaliza espaços
nome = nome.replace(/\s+/g, " ").trim();

// ===== 5) Defaults e pequenos ajustes =====
if (!categoria) {
  // se não informou categoria, heurística:
  // se frase não contém "borda" nem "item", assume "ingrediente"
  categoria = "ingrediente";
}

// Correções simples de termos comuns
const correcoes = {
  "cheedar": "cheddar",
  "cheedar ": "cheddar ",
  "mucarela": "mussarela",
  "muçarela": "mussarela",
};
Object.keys(correcoes).forEach(k => {
  nome = nome.replace(new RegExp("\\b" + k + "\\b", "g"), correcoes[k]);
});

// Saída
return [{
  json: {
    acao,                 // ex.: "adicionar"
    categoria,            // ex.: "borda" | "ingrediente" | "item"
    nome,                 // ex.: "mussarela"
