// 1) minúsculas
let clean = input.toLowerCase();

// 2) remove moeda: "r$", "r $", "$"
clean = clean.replace(/r\s*\$/gi, "").replace(/\$/g, "");

// 3) remove o "r" (ou qualquer letra) deixado ANTES de número/1/2: "r 6", "r 1/2"
clean = clean.replace(/\b[a-zà-ÿ]\s+(?=(\d|1\/2)\b)/gi, "");

// 4) mantém só letras/números/espaço e "/" (tira resto de símbolos)
clean = clean.replace(/[^0-9a-zà-ÿ\s/,.]/g, " ");

// 5) normaliza plurais comuns
clean = clean.replace(/\bpizzas\b/g, "pizza")
             .replace(/\besfirras\b/g, "esfirra");

// 6) palavras de quantidade -> número (antes de "pizza")
clean = clean.replace(/\b(uma|um)\b(?=\s+pizza\b)/g, "1")
             .replace(/\b(duas|dois)\b(?=\s+pizza\b)/g, "2")
             .replace(/\b(três|tres)\b(?=\s+pizza\b)/g, "3");

// 7) "meia" -> "1/2"
clean = clean.replace(/\bmeia\b/g, "1/2");

// 8) corrige "6,19" ou "6.19" -> "1/2 19"
clean = clean.replace(/6[,.](\d{1,2})\b/g, "1/2 $1");

// 9) padrão da transcrição "6 14 e 6 19" -> "1 pizza 1/2 14 e 1/2 19"
clean = clean.replace(/\b6\s+(\d{1,2})(?:\s*[,;]?\s*e\s*)6\s+(\d{1,2})\b/g, "1 pizza 1/2 $1 e 1/2 $2");

// 10) remove vírgulas/pontos remanescentes que grudem nos números (ex.: "17," -> "17")
clean = clean.replace(/[,.]/g, " ");



// 12) compacta espaços
clean = clean.replace(/\s+/g, " ").trim();

return { original: input, comando_normalizado: clean };
