UPDATE pedido_temp p
SET status = 'montagem'
WHERE p.user_id   = {{ $('DadoPedido').item.json.user_id }}
  AND p.pedido_id = {{ $('DadoPedido').item.json.pedido_id }}
  AND EXISTS (
    SELECT 1
    FROM item_pedido_temp i
    WHERE i.user_id   = p.user_id
      AND i.pedido_id = p.pedido_id
      AND i.status    = 'opcoes'
  );

