 const raw = ($json.calcula_pre_pedido || "").replace(/\r\n/g, "\n");
const txt = raw.trim();

if (!txt) {
  return { texto_alinhado: "não existe registro" };
}

const lines = txt.split("\n").map(l => l.trimEnd());

// Se não tiver "Resumo:", devolve o corpo original
const start = lines.findIndex(l => /resumo:/i.test(l));
if (start === -1) {
  return { texto_alinhado: txt };
}

let end = lines.findIndex((l, i) => i > start && (
  /forma de pagamento/i.test(l) || /entrega:/i.test(l) || /total:/i.test(l) || /total de itens/i.test(l)
));
if (end === -1) end = lines.length;

const header = lines.slice(0, start + 1);
const itemLines = lines.slice(start + 1, end).filter(l => l.trim());
const footer = lines.slice(end);

// ---- parsing ajustado ----
const parsed = itemLines.map(line => {
  const match = line.match(/(R\$\s*[\d\.,]+)$/);
  if (!match) return { num: "", desc: line.trim(), valor: "" };

  const valor = match[1].replace("R$", "").trim();
  const left = line.slice(0, match.index).trim();

  // captura do número inicial (ex: (1) Nome ...)
  const numMatch = left.match(/^\((\d+)\)\s*(.*)$/);
  let num = "", desc = left;
  if (numMatch) {
    num = numMatch[1];
    desc = numMatch[2].trim();
  }

  return { num, desc, valor };
});

const maxNum = parsed.reduce((m, o) => Math.max(m, o.num.length), 0);
const maxDesc = parsed.reduce((m, o) => Math.max(m, o.desc.length), 0);

// monta itens alinhados com cabeçalho
const itensAlinhados = [
  "Item ".padEnd(maxNum + 2, " ") +
  "Descrição".padEnd(maxDesc + 4, " ") +
  "Valor"
];

parsed.forEach(o => {
  itensAlinhados.push(
    o.num.padEnd(maxNum + 2, " ") +
    o.desc.padEnd(maxDesc + 4, " ") +
    "R$ " + o.valor
  );
});

// ---- corpo final ----
const corpo = [
  ...header,
  ...itensAlinhados,
  ...footer
].join("\n");

return { texto_alinhado: "\n" + corpo + "\n" };

