 // === Code node (Run Once for All Items) ===
// Entrada: items -> [{ conta_codigo, conta_nome, saldo_mes_anterior, saldo_mes_atual, diferenca }, ...]
// Saída: { html } -> para Convert to File / Gotenberg

const rows = items.map(i => i.json);

// ordena por código de conta (humano)
rows.sort((a, b) => String(a.conta_codigo).localeCompare(String(b.conta_codigo), 'pt-BR', { numeric: true }));

const fmt   = (n) => Number(n || 0).toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 });
const sum   = (key) => rows.reduce((acc, r) => acc + (Number(r[key]) || 0), 0);
const cellN = (n) => `<td class="num ${Number(n) < 0 ? 'neg' : ''}">${fmt(n)}</td>`;

// totais
const totalAnt  = sum('saldo_mes_anterior');
const totalMes  = sum('saldo_mes_atual');
// se a consulta já traz "diferenca" por linha, somamos; senão, calculamos por (mes - anterior)
const totalDiff = rows.some(r => r.hasOwnProperty('diferenca')) ? sum('diferenca') : (totalMes - totalAnt);

// metadata
const geradoEm = new Date().toLocaleString('pt-BR');
const titulo   = 'Balancete de Saldos';
const subtitulo= `Gerado em ${geradoEm}`;

let html = `<!DOCTYPE html>
<html lang="pt-BR">
<head>
<meta charset="UTF-8">
<title>${titulo}</title>
<meta http-equiv="Content-Type" content="text/html; charset=UTF-8">
<style>
  :root{
    --primary:#1e88e5;
    --zebra:#f5f9ff;
    --border:#e5e9f2;
    --text:#2c3e50;
    --muted:#6b7a90;
    --total-bg:#eef3fb;
    --neg:#c0392b;
  }
  @page { margin: 20mm 12mm; }
  * { box-sizing: border-box; }
  body{
    font-family: "Segoe UI", Arial, sans-serif;
    color: var(--text);
    margin: 0;
    padding: 24px;
    background: #fff;
  }
  header{
    text-align:center;
    margin-bottom: 14px;
  }
  h1{
    margin: 0 0 4px 0;
    font-size: 20px;
    font-weight: 700;
    letter-spacing: .3px;
  }
  .sub{
    color: var(--muted);
    font-size: 12px;
  }
  table{
    width:100%;
    border-collapse: collapse;
    font-size: 12px;
    margin-top: 8px;
  }
  thead th{
    background: var(--primary);
    color: #fff;
    padding: 8px 10px;
    text-align: left;
    border: 1px solid var(--primary);
    white-space: nowrap;
  }
  tbody td{
    border: 1px solid var(--border);
    padding: 7px 10px;
    vertical-align: top;
  }
  tbody tr:nth-child(even){ background: var(--zebra); }
  td.cod   { width: 90px; font-variant-numeric: tabular-nums; }
  td.nome  { width: auto; }
  .num{
    text-align: right;
    font-variant-numeric: tabular-nums;
    white-space: nowrap;
  }
  .neg{ color: var(--neg); font-weight: 600; }
  tfoot td{
    border: 1px solid var(--border);
    background: var(--total-bg);
    font-weight: 700;
    padding: 8px 10px;
  }
  .tot-label{ text-align:right; }
</style>
</head>
<body>
  <header>
    <h1>${titulo}</h1>
    <div class="sub">${subtitulo}</div>
  </header>

  <table>
    <thead>
      <tr>
        <th>Código</th>
        <th>Conta</th>
        <th>Saldo mês anterior</th>
        <th>Saldo do mês</th>
        <th>Diferença</th>
      </tr>
    </thead>
    <tbody>
`;

for (const r of rows) {
  html += `
      <tr>
        <td class="cod">${r.conta_codigo ?? ''}</td>
        <td class="nome">${r.conta_nome ?? ''}</td>
        ${cellN(r.saldo_mes_anterior)}
        ${cellN(r.saldo_mes_atual)}
        ${cellN(r.diferenca ?? (Number(r.saldo_mes_atual||0) - Number(r.saldo_mes_anterior||0)))}
      </tr>
  `;
}

html += `
    </tbody>
    <tfoot>
      <tr>
        <td colspan="2" class="tot-label">Totais</td>
        ${cellN(totalAnt)}
        ${cellN(totalMes)}
        ${cellN(totalDiff)}
      </tr>
    </tfoot>
  </table>
</body>
</html>`;

return [{ json: { html } }];
