  // üîπ N√∫mero do item = √∫ltimo n√∫mero do texto (depois da palavra pizza/borda)
  const mNums = t.match(/\d+/g);
  let numero = null;
  if (mNums && mNums.length > 1) {
    numero = parseInt(mNums[mNums.length - 1], 10);
  } else if (mNums) {
    // se s√≥ tiver um n√∫mero e ele n√£o for a quantidade
    numero = quantidade > 1 ? null : parseInt(mNums[0], 10);
  }

  // üîπ Categoria
  const categoria = /borda/.test(t)
    ? "borda"
    : /pizza/.test(t)
    ? "pizza"
    : "item";

  // üîπ Tamanho
  const tamanho = /grande/.test(t)
    ? "grande"
    : /m[e√©]dia/.test(t)
    ? "media"
    : /pequena|peq/.test(t)
    ? "pequena"
    : null;

  return {
    quantidade,
    categoria,
    numero,
    tamanho,
    texto_original: txt,
    fracionada: $input.first().json.categoria,
    referencia: $input.first().json.referencia,
    id_empresa:$('When Executed by Another Workflow').first().json.empresa_id,
    user_id: $('When Executed by Another Workflow').first().json.user_id,
    pedido_id:$('When Executed by Another Workflow').first().json.pedido_id
  };
}

return entradas.map(e => ({ json: parseLinha(e) }));
