 const raw = ($json.calcula_pre_pedido || "").replace(/\r\n/g, "\n");
const txt = raw.trim();

if (!txt) {
  return { texto_alinhado: "n√£o existe registro" };
}

const lines = txt.split("\n").map(l => l.trimEnd());

// Se n√£o tiver "Resumo:", devolve o corpo original
const start = lines.findIndex(l => /resumo:/i.test(l));
if (start === -1) {
  return { texto_alinhado: txt };
}

let end = lines.findIndex((l, i) => i > start && (
  /forma de pagamento/i.test(l) || /entrega:/i.test(l) || /total:/i.test(l) || /total de itens/i.test(l)
));
if (end === -1) end = lines.length;

const header = lines.slice(0, start + 1);
const itemLines = lines.slice(start + 1, end).filter(l => l.trim());
const footer = lines.slice(end);

// ---- parsing ajustado ----
const parsed = itemLines.map(line => {
  const match = line.match(/(R\$\s*[\d\.,]+)$/);
  if (!match) return { num: "", desc: line.trim(), valor: "" };

  const valor = match[1].replace("R$", "").trim();
  const left = line.slice(0, match.index).trim();

  // captura do n√∫mero inicial (ex: (1) Nome ...)
  const numMatch = left.match(/^\((\d+)\)\s*(.*)$/);
  let num = "", desc = left;
  if (numMatch) {
    num = numMatch[1];
    desc = numMatch[2].trim();
  }

  return { num, desc, valor };
});

const maxNum = parsed.reduce((m, o) => Math.max(m, o.num.length), 0);
const maxDesc = parsed.reduce((m, o) => Math.max(m, o.desc.length), 0);

// monta itens alinhados com cabe√ßalho
const itensAlinhados = [
  "Item ".padEnd(maxNum + 2, " ") +
  "Descri√ß√£o".padEnd(maxDesc + 4, " ") +
  "Valor"
];

parsed.forEach(o => {
  itensAlinhados.push(
    o.num.padEnd(maxNum + 2, " ") +
    o.desc.padEnd(maxDesc + 4, " ") +
    "R$ " + o.valor
  );
});

// ---- corpo final ----
const corpo = [
  ...header,
  ...itensAlinhados,
  ...footer
].join("\n");

return { texto_alinhado: "```\n" + corpo + "\n```" };

 const input =  $input.first().json.chatInput || "";

// Normaliza
const frase = input.toLowerCase().replace(/\s+/g, " ").trim();

const result = {
  pizzas_inteiras: [],
  pizzas_fracionadas: [],
  bebidas: [],
  bordas: []
};

// Quebra em peda√ßos por " e ", " , ", " + " etc
const partes = frase.split(/,| e | \+ | \/ /).map(p => p.trim()).filter(Boolean);

for (let p of partes) {
  let qtd = 1;
  const qtdMatch = p.match(/^(\d+)/);
  if (qtdMatch) qtd = parseInt(qtdMatch[1]);

  // üçï Fracionada
  if (p.includes("1/2")) {
    const fracoes = p.split(/1\/2/).map(f => f.trim()).filter(Boolean);
    if (fracoes.length === 2) {
      result.pizzas_fracionadas.push({
        quantidade: qtd,
        metade1: isNaN(fracoes[0]) ? fracoes[0] : parseInt(fracoes[0]),
        metade2: isNaN(fracoes[1]) ? fracoes[1] : parseInt(fracoes[1]),
        tamanho: "media"
      });
    }
  }

  // üßÄ Borda
  else if (p.includes("borda")) {
    result.bordas.push({
      quantidade: qtd,
      nome: p.replace(/borda|pizza|item|\d+/gi, "").trim(),
      pizza_referencia: ""
    });
  }

  // ü•§ Bebidas
  else if (p.includes("coca") || p.includes("refrigerante") || p.includes("guaran√°") || p.includes("suco")) {
    let volume = "";
    if (p.includes("2l")) volume = "2l";
    else if (p.includes("350ml") || p.includes("lata")) volume = "350ml";
    else if (p.includes("330ml") || p.includes("long neck")) volume = "330ml";

    result.bebidas.push({
      quantidade: qtd,
      nome: p.replace(/\d+|coca|cola|refrigerante|guaran√°|suco|lata|long neck|ml|l/gi, "").trim(),
      volume: volume || "2l"
    });
  }

  // üçï Pizza inteira
  else if (p.includes("pizza")) {
    let tamanho = "media";
    if (p.includes("grande")) tamanho = "grande";
    else if (p.includes("brotinho")) tamanho = "brotinho";

    const numeroMatch = p.match(/(\d+)/);
    if (numeroMatch) {
      result.pizzas_inteiras.push({
        quantidade: qtd,
        numero: parseInt(numeroMatch[1]),
        tamanho
      });
    } else {
      result.pizzas_inteiras.push({
        quantidade: qtd,
        nome: p.replace(/\d+|pizza|grande|media|brotinho/gi, "").trim(),
        tamanho
      });
    }
  }
}

return [{ json: result }];

 // ===== Emojis =====
const HDR_EMOJI  = 'üîπ';
const DESC_EMOJI = '‚ñ´Ô∏è';

// -------- Helpers --------
function toNum(x) {
  if (typeof x === 'number' && isFinite(x)) return x;
  if (x == null) return 0;
  const s = String(x).replace(/[^\d,.-]/g, '').replace(/\.(?=\d{3}(?:\D|$))/g, '').replace(',', '.');
  const n = Number(s);
  return isFinite(n) ? n : 0;
}
function moneyTrim(n) {
  const s = toNum(n).toFixed(2).replace(/(\.\d*?)0+$/, '$1').replace(/\.$/, '');
  return s;
}
function clean(txt) {
  return String(txt || '')
    .replace(/\uFFFD/g, '')
    .normalize('NFD').replace(/[\u0300-\u036f]/g,'')
    .replace(/\s*-\s*/g, ' - ')
    .replace(/[ \t]+/g, ' ')
    .trim();
}
const bold = s => `*${s}*`;

// -------- Infer√™ncia de Categoria & Tipo --------
const BEBIDAS_RX = /\b(coca(?:\s*cola)?|guaran[a√°]|heineken|bud(?:weiser)?|cerveja|√°gua|agua|fanta|sprite|pepsi|refrigerante|long\s*neck|lata)\b/i;
const BORDA_RX   = /\bborda\b/i;
const ESFIRRA_RX = /\besfirra\b/i;

const DOCES_RX = /\b(chocolate|nutella|goiabada|doce\s+de\s+leite|brigadeiro|beijinho|romeu\s*e\s*julieta|banana|morango|pa√ßoca|pacoca|prest√≠gio|prestigio)\b/i;

function guessCategoria(nome, desc, cat) {
  if (cat && String(cat).trim()) return String(cat).toLowerCase(); // j√° veio do backend
  const hay = `${nome} ${desc}`;
  if (BEBIDAS_RX.test(hay)) return 'bebida';
  if (BORDA_RX.test(hay))   return 'borda';
  if (ESFIRRA_RX.test(hay)) return 'esfirra';
  return 'pizza'; // default
}

function guessTipo(nome, desc, tipo) {
  if (tipo && String(tipo).trim()) return String(tipo).toLowerCase();
  const hay = `${nome} ${desc}`;
  return DOCES_RX.test(hay) ? 'doce' : 'salgada';
}

// -------- Entrada --------
const items = $input.all();
let rows = [];
if (items.length > 1) rows = items.map(i => i.json);
else {
  let p = items?.[0]?.json ?? {};
  if (p && typeof p === 'object' && 'result' in p) p = p.result;
  rows = Array.isArray(p) ? p : (p && typeof p === 'object' ? [p] : []);
}

// -------- Sa√≠da formatada --------
const lines = rows.map(r => {
  const numero = r.numero ?? r.id ?? r.code ?? '';
  const nome   = clean(r.nome ?? r.name ?? 'Item');
  const desc   = clean(r.descricao ?? r.description ?? '');

  const g = r.preco_grande ?? r.precoG ?? r.preco_g;
  const m = r.preco_media  ?? r.precoM ?? r.preco_m;

  const categoria = guessCategoria(nome, desc, r.categoria);
  const tipo      = guessTipo(nome, desc, r.tipo);

  const parts = [];
  if (g != null && g !== '') parts.push(`(G) R$${moneyTrim(g)}`);
  if (m != null && m !== '') parts.push(`(M) R$${moneyTrim(m)}`);
  const precoStr = parts.join(' / ') || 'R$0';

  const header = `${HDR_EMOJI} ${ bold(`(${numero})`) } ${nome} ‚Äì ${bold(precoStr)} ${bold(` [${categoria}] [${tipo}]`)}`;
  const body   = `${DESC_EMOJI} Descri√ß√£o: ${desc || '-'}`
  return `${header}\n${body}`;
});

return [{
  json: {Op√ß√µes: "Op√ß√µes dos itens n√£o encontrados\n",
    descricao: lines.join('\n'),
    user_id: $json.user_id ?? null,
    pedido_id: $json.pedido_id ?? null
  }
}];

 const descricao = $json.descricao || "";
return {
  mensagem: "```
" +  "Suas op√ß√µes dos itens n√£o encontrados:
Dois exemplos validos: 1 pizza 32 grande ou 1 pizza 1/2 14 e meia 12
Escolha sua op√ß√£o abaixo ‚¨áÔ∏è

" +   descricao + " ```"
};

